            u1 = mulmod(u1, u2, p);
            w = addmod(w, p - u1, p);

            x2 = mulmod(u, w, p);

            u3 = mulmod(u2, u, p);
            u0 = mulmod(u0, u2, p);
            u0 = addmod(u0, p - w, p);
